import { Component, OnInit, HostBinding } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

import { Subscription } from 'rxjs';
import { switchMap } from 'rxjs/operators';

import { IResolvedRouteData, ResolverHelper } from '../../../utils/resolver-helper';

import { FirebaseProfileModel } from './firebase-profile.model';
import { FirebaseAuthService } from '../firebase-auth.service';


@Component({
  selector: 'app-firebase-profile',
  templateUrl: './firebase-profile.page.html',
  styleUrls: [
    './styles/firebase-profile.page.scss',
    './styles/firebase-profile.shell.scss'
  ]
})
export class FirebaseProfilePage implements OnInit {
  // ? Gather all component subscription in one place. Can be one Subscription or multiple (chained using the Subscription.add() method)
  subscriptions: Subscription;
  user: FirebaseProfileModel;

  @HostBinding('class.is-shell') get isShell() {
    return (this.user && this.user.isShell) ? true : false;
  }

  constructor(
    private router: Router,
    private route: ActivatedRoute,
    public authService: FirebaseAuthService
  ) {}

  ngOnInit() {
    this.subscriptions = this.route.data
    .pipe(
      // ? Extract data for this page
      switchMap((resolvedRouteData: IResolvedRouteData<FirebaseProfileModel>) => {
        return ResolverHelper.extractData<FirebaseProfileModel>(resolvedRouteData.data, FirebaseProfileModel);
      })
    )
    .subscribe({
      next: (state) => {
        this.user = state;
      },
      error: (error) => console.log(error)
    });
  }

  public async signOut(): Promise<void> {
    try {
      // * 1. Sign out on the native layer
      await this.authService.signOut()
      .then((result) => {
        // ? Sign-out successful
        // ? Replace state as we are no longer authorized to access profile page
        this.router.navigate(['firebase/auth/sign-in'], { replaceUrl: true });
      })
      .catch((error) => {
        console.log('userProfile - signOut() - error', error);
      });
    } finally {
      console.log('userProfile - signOut() - finally');
    }
  }

  // ? NOTE: Ionic only calls ngOnDestroy if the page was popped (ex: when navigating back)
  // ? Since ngOnDestroy might not fire when you navigate from the current page, use ionViewWillLeave to cleanup Subscriptions
  ionViewWillLeave(): void {
    this.subscriptions.unsubscribe();
  }
}
<ion-header class="ion-no-border">
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-back-button defaultHref="app/categories"></ion-back-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content class="user-content">
  <ion-row>
    <ion-col class="ion-no-padding">
      <app-image-shell class="user-image add-overlay" [display]="'cover'" animation="spinner" [src]="user?.image ?? './assets/sample-images/avatar.png'" [alt]="'item image'">
        <app-aspect-ratio [ratio]="{w:1, h:1}">
        </app-aspect-ratio>
      </app-image-shell>
    </ion-col>
  </ion-row>

  <ion-row class="user-details">
    <ion-col class="ion-no-padding details-wrapper">
      <h2 class="user-name">
        <app-text-shell [data]="user?.name"></app-text-shell>
        <ion-icon name="checkmark-circle" color="bright-pink"></ion-icon>
      </h2>
      <span class="user-role">
        <app-text-shell [data]="user?.role"></app-text-shell>
      </span>
      <p class="user-description">
        <app-text-shell lines="3" [data]="user?.description"></app-text-shell>
      </p>
    </ion-col>
  </ion-row>

  <ion-row class="user-preferences-wrapper">
    <ion-col size="12">
      <h4 class="preference-name">Mobile</h4>
      <p class="preference-value">
        <app-text-shell [data]="user?.phoneNumber"></app-text-shell>
      </p>
    </ion-col>
    <ion-col size="12">
      <h4 class="preference-name">Email</h4>
      <p class="preference-value">
        <app-text-shell [data]="user?.email"></app-text-shell>
      </p>
    </ion-col>
    <ion-col size="12">
      <h4 class="preference-name">Auth Provider</h4>
      <p class="preference-value">
        <app-text-shell [data]="user?.provider"></app-text-shell>
      </p>
    </ion-col>
    <ion-col size="12">
      <ion-button class="sign-out-btn" expand="block" fill="outline" color="bright-blue" (click)="signOut()">Sign out</ion-button>
    </ion-col>
  </ion-row>
</ion-content>
