import { Component, OnInit, HostBinding, Inject, PLATFORM_ID } from '@angular/core';
import { VideoPlaylistModel } from './video-playlist.model';
import { VgApiService } from '@videogular/ngx-videogular/core';
import { ActivatedRoute } from '@angular/router';
import { Share } from '@capacitor/share';
import { switchMap } from 'rxjs/operators';
import { isPlatformBrowser } from '@angular/common';

@Component({
  selector: 'app-video-playlist',
  templateUrl: './video-playlist.page.html',
  styleUrls: [
    './styles/video-playlist.page.scss',
    './styles/video-playlist.shell.scss'
  ]
})
export class VideoPlaylistPage implements OnInit {
  ssr = true;
  start_playing = false;
  api: VgApiService;
  video_playlist_model: VideoPlaylistModel;

  @HostBinding('class.is-shell') get isShell() {
    return (this.video_playlist_model && this.video_playlist_model.isShell) ? true : false;
  }

  constructor(
    private route: ActivatedRoute,
    @Inject(PLATFORM_ID) private platformId: object
  ) { }

  ngOnInit(): void {
    // In SSR show a placeholder for the <vg-player>
    if (isPlatformBrowser(this.platformId)) {
      this.ssr = false;
    }

    this.route.data
    .pipe(
      // Extract data for this page
      switchMap((resolvedRouteData) => resolvedRouteData['data'].state)
    )
    .subscribe({
      next: (state: any) => {
        this.video_playlist_model = state;
        if (!state.isShell) {
          this.video_playlist_model.video_playlist = state.videos;
          this.video_playlist_model.selected_video = state.videos[0];
        }
      },
      error: (error) => console.log(error)
    });
  }

  playMedia(media) {
    // Check if this media is not the same we are currently playing
    if (media !== this.video_playlist_model.selected_video) {
      // Change sources
      this.video_playlist_model.selected_video = media;
      // When changing sources we wait until the metadata is loaded and then we start playing the video
    }
  }

  onPlayerReady(api: VgApiService) {
    this.api = api;
    this.api.getDefaultMedia().subscriptions.loadedMetadata.subscribe(this.playVideo.bind(this));
  }

  playVideo() {
    if (this.start_playing) {
      this.api.play();
    } else {
      this.start_playing = true;
    }
  }

  shareMedia() {
    const selectedVideo = this.video_playlist_model.selected_video;
    Share.share({
      title: selectedVideo.title,
      text: selectedVideo.description,
      url: 'https://ionicthemes.com/',
      dialogTitle: 'Share Media'
    })
    .then(() => console.log('Successful share'))
    .catch((error) => console.log('Error sharing', error));
  }
}
<ion-header>
  <ion-toolbar color="primary">
    <ion-buttons slot="start">
      <ion-menu-button></ion-menu-button>
    </ion-buttons>
    <ion-title>Video Playlist</ion-title>
    <ion-buttons slot="end">
      <ion-button (click)="shareMedia()">
        <ion-icon slot="icon-only" name="share"></ion-icon>
      </ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content class="video-playlist-content">
  <app-aspect-ratio [ratio]="{w: 16, h: 9}">
    <!-- SSR placeholder -->
    <video *ngIf="ssr" class="ssr-video" controls preload="auto">
      <source *ngFor="let video of video_playlist_model?.selected_video?.sources" [src]="video.src" [type]="video.type">
    </video>
    <!-- In the Browser, show the <vg-player> -->
    <vg-player *ngIf="!ssr" class="video-player" (onPlayerReady)="onPlayerReady($event)">
			<video controls #player_video [vgMedia]="player_video" id="player-video" preload="auto">
	      <source *ngFor="let video of video_playlist_model?.selected_video?.sources" [src]="video.src" [type]="video.type">
	    </video>
    </vg-player>
  </app-aspect-ratio>

  <section class="video-data-section">
    <h3 class="video-title">
      <app-text-shell animation="gradient" [data]="video_playlist_model?.selected_video?.title"></app-text-shell>
    </h3>
    <p class="video-description">
      <app-text-shell lines="5" animation="gradient" [data]="video_playlist_model?.selected_video?.description"></app-text-shell>
    </p>
  </section>

  <section class="video-playlist-section">
    <ion-row class="playlist-item-row" *ngFor="let video of video_playlist_model?.video_playlist">
      <ion-col size="4">
        <app-image-shell [display]="'cover'" class="video-image" animation="spinner" [src]="video.thumbnail" [alt]="'video thumbnail'" (click)="playMedia(video)">
          <app-aspect-ratio [ratio]="{w:3, h: 2}">
            <a class="play-icon-wrapper" (click)="playMedia(video)">
              <ion-icon class="play-icon" name="play"></ion-icon>
            </a>
          </app-aspect-ratio>
        </app-image-shell>
      </ion-col>
      <ion-col size="8" class="video-info-wrapper">
        <h4 class="image-title">
          <app-text-shell animation="gradient" [data]="video.title"></app-text-shell>
        </h4>
        <p class="image-description">
          <app-text-shell lines="3" animation="gradient" [data]="video.description"></app-text-shell>
        </p>
      </ion-col>
    </ion-row>
  </section>
</ion-content>
