import { Component, OnInit, HostBinding } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { AlertController } from '@ionic/angular';
import { IonicSwiper } from "@ionic/angular";
import SwiperCore, { Pagination } from "swiper";

import { Subscription } from 'rxjs';
import { switchMap } from 'rxjs/operators';

import { IResolvedRouteData, ResolverHelper } from '../../utils/resolver-helper';
import { FashionDetailsModel } from './fashion-details.model';

SwiperCore.use([Pagination, IonicSwiper]);


@Component({
  selector: 'app-fashion-details',
  templateUrl: './fashion-details.page.html',
  styleUrls: [
    './styles/fashion-details.page.scss',
    './styles/fashion-details.shell.scss',
    './styles/fashion-details.ios.scss',
    './styles/fashion-details.md.scss'
  ]
})
export class FashionDetailsPage implements OnInit {
  // Gather all component subscription in one place. Can be one Subscription or multiple (chained using the Subscription.add() method)
  subscriptions: Subscription;

  details: FashionDetailsModel;
  colorVariants = [];
  sizeVariants = [];

  slidesOptions: any = {
    zoom: {
      toggle: false // Disable zooming to prevent weird double tap zomming on slide images
    }
  };

  @HostBinding('class.is-shell') get isShell() {
    return (this.details && this.details.isShell) ? true : false;
  }

  constructor(
    private route: ActivatedRoute,
    public alertController: AlertController
  ) { }

  ngOnInit(): void {

    this.subscriptions = this.route.data
    .pipe(
      // Extract data for this page
      switchMap((resolvedRouteData: IResolvedRouteData<FashionDetailsModel>) => {
        return ResolverHelper.extractData<FashionDetailsModel>(resolvedRouteData.data, FashionDetailsModel);
      })
    )
    .subscribe({
      next: (state) => {
        this.details = state;

        this.colorVariants = this.details.colorVariants
        .map(item =>
          ({
            name: item.name,
            type: 'radio',
            label: item.name,
            value: item.value,
            checked: item.default
          })
        );

        this.sizeVariants = this.details.sizeVariants
        .map(item =>
          ({
            name: item.name,
            type: 'radio',
            label: item.name,
            value: item.value,
            checked: item.default
          })
        );
      },
      error: (error) => console.log(error)
    });
  }

  async openColorChooser() {
    const alert = await this.alertController.create({
      header: 'Color',
      inputs: this.colorVariants,
      cssClass: 'variant-alert color-chooser',
      buttons: [
        {
          text: 'Cancel',
          role: 'cancel',
          cssClass: 'secondary',
          handler: () => {
            console.log('Confirm Cancel');
          }
        }, {
          text: 'OK',
          handler: () => {
            console.log('Confirm Ok');
          }
        }
      ]
    });

    await alert.present();
  }

  async openSizeChooser() {
    const alert = await this.alertController.create({
      header: 'Size',
      inputs: this.sizeVariants,
      cssClass: 'variant-alert size-chooser',
      buttons: [
        {
          text: 'Cancel',
          role: 'cancel',
          cssClass: 'secondary',
          handler: () => {
            console.log('Confirm Cancel');
          }
        }, {
          text: 'OK',
          handler: (selectedValue) => {
            console.log('Selected Value', selectedValue);
          }
        }
      ]
    });

    await alert.present();
  }

  // NOTE: Ionic only calls ngOnDestroy if the page was popped (ex: when navigating back)
  // Since ngOnDestroy might not fire when you navigate from the current page, use ionViewWillLeave to cleanup Subscriptions
  ionViewWillLeave(): void {
    this.subscriptions.unsubscribe();
  }
}
<ion-header>
  <ion-toolbar color="primary">
    <ion-buttons slot="start">
      <ion-back-button defaultHref="app/categories/fashion"></ion-back-button>
    </ion-buttons>
    <ion-title>Fashion Details</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content class="fashion-details-content">
  <ion-row class="slider-row">
    <swiper [pagination]="true" [config]="slidesOptions" class="details-slides">
      <ng-template swiperSlide *ngFor="let image of details?.showcaseImages">
        <ion-row class="slide-inner-row">
          <app-image-shell [display]="'cover'" animation="spinner" class="showcase-image" [ngClass]="{'centered-image': (image.type === 'square'), 'fill-image': (image.type === 'fill')}" [src]="image.source">
            <app-aspect-ratio [ratio]="{w:64, h:50}">
            </app-aspect-ratio>
          </app-image-shell>
        </ion-row>
      </ng-template>
    </swiper>
  </ion-row>
  <div class="description-wrapper">
    <h3 class="details-name">
      <app-text-shell animation="gradient" [data]="details?.name"></app-text-shell>
    </h3>
    <span class="details-brand">
      <app-text-shell animation="gradient" [data]="details?.brand"></app-text-shell>
    </span>
    <span class="details-price">
      <app-text-shell animation="gradient" [data]="details?.price | currency"></app-text-shell>
    </span>
    <span class="details-sale-price">
      <app-text-shell animation="gradient" [data]="details?.salePrice | currency"></app-text-shell>
    </span>
  </div>
  <ion-row class="details-purchase-options-row">
    <ion-col size="12" class="aux-action-col">
      <ion-button class="size-chart-btn" color="medium" size="small" fill="clear">Size Chart</ion-button>
    </ion-col>
    <ion-col size="6">
      <ion-button class="select-variant-btn" color="medium" expand="block" fill="outline" (click)="openColorChooser()">Color</ion-button>
    </ion-col>
    <ion-col size="6">
      <ion-button class="select-variant-btn" color="medium" expand="block" fill="outline" (click)="openSizeChooser()">Size</ion-button>
    </ion-col>
    <ion-col size="12" class="main-call-to-action-col">
      <ion-button class="add-to-cart-btn" color="dark" expand="block" fill="solid">Add to cart</ion-button>
    </ion-col>
  </ion-row>
  <div class="details-description-wrapper">
    <h3 class="detail-title">About me</h3>
    <p class="details-description">
      <app-text-shell animation="gradient" [data]="details?.description" lines="5"></app-text-shell>
    </p>
  </div>
  <div class="details-description-wrapper">
    <h3 class="detail-title">Looking after me</h3>
    <p class="details-description">
      <app-text-shell animation="gradient" [data]="details?.careInstructions" lines="3"></app-text-shell>
    </p>
  </div>
  <div class="details-description-wrapper">
    <h3 class="detail-title">Product code</h3>
    <span class="details-description product-code">
      <app-text-shell animation="gradient" [data]="details?.upc"></app-text-shell>
    </span>
  </div>
  <!-- Related Products -->
  <div class="related-products-wrapper" *ngIf="details?.relatedProducts && details.relatedProducts.length > 0">
    <h3 class="detail-alt-title">Why Don't you try?</h3>
    <ion-row class="related-products-list">
      <ion-col size="6" class="related-product-item" *ngFor="let relatedProduct of details?.relatedProducts">
        <div class="item-image-wrapper">
          <a class="image-anchor">
            <app-aspect-ratio [ratio]="{w: 3, h: 4}">
              <app-image-shell class="related-product-image" animation="spinner" [src]="relatedProduct.image" [alt]="'related image'"></app-image-shell>
            </app-aspect-ratio>
          </a>
        </div>
        <div class="item-details-wrapper">
          <h4 class="item-name">
            <a class="name-anchor">
              <app-text-shell animation="gradient" [data]="relatedProduct.name"></app-text-shell>
            </a>
          </h4>
          <span class="item-sale-price">
            <app-text-shell animation="gradient" [data]="relatedProduct.salePrice | currency"></app-text-shell>
          </span>
        </div>
      </ion-col>
    </ion-row>
  </div>
</ion-content>
