import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

// You can also use a Class object as a shell model
import { ShowcaseShellModel } from '../../showcase-shell.model';

@Component({
  selector: 'app-showcase-blocking-resolvers',
  templateUrl: './blocking-resolvers.page.html',
  styleUrls: ['./blocking-resolvers.page.scss']
})
export class BlockingResovlersPage implements OnInit {
  // We will assign data coming from the Route Resolver to this property
  routeResolveData: ShowcaseShellModel;

  constructor(
    private route: ActivatedRoute
  ) { }

  ngOnInit(): void {
    console.log('Showcase Blocking Resovlers - ngOnInit()');

    if (this.route && this.route.data) {
      const dataObservable = this.route.data;
      console.log('Showcase Blocking Resovlers - Route Resolve Observable => dataObservable: ', dataObservable);

      if (dataObservable) {
        dataObservable.subscribe(observableValue => {
          const pageData: ShowcaseShellModel = observableValue['data'];
          // eslint-disable-next-line max-len
          console.log('Showcase Blocking Resovlers - Subscribe to dataObservable (will emmit just one value) => PageData (' + ((pageData && pageData.isShell) ? 'SHELL' : 'REAL') + '): ', pageData);
          if (pageData) {
            this.routeResolveData = pageData;
          }
        });
      } else {
        console.warn('No dataObservable coming from Route Resolver data');
      }
    } else {
      console.warn('No data coming from Route Resolver');
    }
  }
}
<ion-header>
  <ion-toolbar color="primary">
    <ion-buttons slot="start">
      <ion-back-button defaultHref="showcase/route-resolvers-ux"></ion-back-button>
    </ion-buttons>
    <ion-title>
      Blocking Resovler Showcase
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content class="showcase-content">
  <section class="showcase-section">
    <p>
      Notice how the UX degrades when using <b>Blocking</b> Route Resolvers in Angular
    </p>
    <ion-row style="background-color: #FFF; padding: 10px;">
      <ion-col size="12">
        <div [ngStyle]="{'background-image': 'url(' + routeResolveData?.cover + ')'}" style="background-size: cover; background-repeat: no-repeat;">
          <app-aspect-ratio [ratio]="{w:2, h:1}">
            <h4 style="margin: 10px; text-align: center; color: #FFF;">Heading on top of a cover image</h4>
          </app-aspect-ratio>
        </div>
      </ion-col>
      <ion-col size="4">
        <app-aspect-ratio [ratio]="{w:1, h:1}">
          <img [src]="routeResolveData?.image" alt="Sample Image" style="width: 100%; height: 100%;"/>
        </app-aspect-ratio>
      </ion-col>
      <ion-col size="8">
        <h3 style="margin-top: 0px;">
          {{ routeResolveData?.title }}
        </h3>
        <p>
          {{ routeResolveData?.description }}
        </p>
      </ion-col>
    </ion-row>
  </section>
</ion-content>
