import { Component } from '@angular/core';

import * as dayjs from 'dayjs';

@Component({
  selector: 'app-showcase-custom-components',
  templateUrl: './custom-components.page.html',
  styleUrls: ['./custom-components.page.scss']
})
export class CustomComponentsPage {
  // Relative date (better to showcase UI micro-interactions)
  countdownDate: string = dayjs().add(1, 'day').add(8, 'hour').add(10, 'second').format('MM/DD/YYYY HH:mm:ss') as string;
  // Instead of hardcoded one
  // countdownDate = '12/01/2018';

  constructor() { }
}
<ion-header>
  <ion-toolbar color="primary">
    <ion-buttons slot="start">
      <ion-menu-button></ion-menu-button>
    </ion-buttons>
    <ion-title>
      Custom Components
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content class="showcase-content">
  <ion-item-divider sticky="true">
    <ion-label>Checkbox Wrapper</ion-label>
  </ion-item-divider>
  <section class="showcase-section">
    <p>
      This is a super straightforward component whose purpose is to wrap an <code>&lt;ion-checkbox&gt;</code> element to enable accessing the checkbox state from its parents.
    </p>
<pre>
&lt;app-checkbox-wrapper class="custom-checkbox"&gt;
  &lt;ion-checkbox checked=true&gt;&lt;/ion-checkbox&gt;
  &lt;ion-label&gt;Sample Checkbox&lt;/ion-label&gt;
&lt;/app-checkbox-wrapper&gt;
</pre>
<pre>
app-checkbox-wrapper.custom-checkbox &#123;
  background: #CCC;
  color: #000;

  &.checkbox-checked &#123;
    background: #00AFFF;
    color: #FFF;
  &#125;
&#125;
</pre>
    <app-checkbox-wrapper class="custom-checkbox">
      <ion-checkbox checked=true></ion-checkbox>
      <ion-label>Sample Checkbox</ion-label>
    </app-checkbox-wrapper>
  </section>

  <ion-item-divider sticky="true">
    <ion-label>Show/Hide Password</ion-label>
  </ion-item-divider>
  <section class="showcase-section">
    <p>
      By wrapping an <code>&lt;ion-input&gt;</code> element with this component, you can enhance the UX and add a show/hide password functionality.
    </p>
<pre>
&lt;app-show-hide-password&gt;
  &lt;ion-input type="password" placeholder="Try setting a password" value="It's a secret!"&gt;&lt;/ion-input&gt;
&lt;/app-show-hide-password&gt;
</pre>
    <app-show-hide-password>
      <ion-input type="password" placeholder="Try setting a password" value="It's a secret!"></ion-input>
    </app-show-hide-password>
  </section>

  <ion-item-divider sticky="true">
    <ion-label>Countdown Timer</ion-label>
  </ion-item-divider>
  <section class="showcase-section">
    <p>
      This components shows the remaining time (in days, hours, minutes or seconds) to a given date.
    </p>
    <p>
      A few examples on how to use this component:
    </p>
    <h5>From days to seconds</h5>
<pre>
&lt;app-countdown-timer <b>[units]="&#123;from: 'day', to: 'second'&#125;"</b> [end]="countdownDate"&gt;&lt;/app-countdown-timer&gt;
</pre>
    <app-countdown-timer [end]="countdownDate" [units]="{from: 'day', to: 'second'}"></app-countdown-timer>
    <h5>From days to minutes</h5>
<pre>
&lt;app-countdown-timer <b>[units]="&#123;from: 'day', to: 'minute'&#125;"</b> [end]="countdownDate"&gt;&lt;/app-countdown-timer&gt;
</pre>
    <app-countdown-timer [end]="countdownDate" [units]="{from: 'day', to: 'minute'}"></app-countdown-timer>
    <h5>From hours to seconds</h5>
<pre>
&lt;app-countdown-timer <b>[units]="&#123;from: 'hour', to: 'second'&#125;"</b> [end]="countdownDate"&gt;&lt;/app-countdown-timer&gt;
</pre>
    <app-countdown-timer [end]="countdownDate" [units]="{from: 'hour', to: 'second'}"></app-countdown-timer>
    <h5>From hours to minutes</h5>
<pre>
&lt;app-countdown-timer <b>[units]="&#123;from: 'hour', to: 'minute'&#125;"</b> [end]="countdownDate"&gt;&lt;/app-countdown-timer&gt;
</pre>
    <app-countdown-timer [end]="countdownDate" [units]="{from: 'hour', to: 'minute'}"></app-countdown-timer>
    <h5>From minutes to seconds</h5>
<pre>
&lt;app-countdown-timer <b>[units]="&#123;from: 'minute', to: 'second'&#125;"</b> [end]="countdownDate"&gt;&lt;/app-countdown-timer&gt;
</pre>
    <app-countdown-timer [end]="countdownDate" [units]="{from: 'minute', to: 'second'}"></app-countdown-timer>
    <h5>Just days</h5>
<pre>
&lt;app-countdown-timer <b>[units]="&#123;from: 'day', to: 'day'&#125;"</b> [end]="countdownDate"&gt;&lt;/app-countdown-timer&gt;
</pre>
    <app-countdown-timer [end]="countdownDate" [units]="{from: 'day', to: 'day'}"></app-countdown-timer>
    <h5>Just hours</h5>
<pre>
&lt;app-countdown-timer <b>[units]="&#123;from: 'hour', to: 'hour'&#125;"</b> [end]="countdownDate"&gt;&lt;/app-countdown-timer&gt;
</pre>
    <app-countdown-timer [end]="countdownDate" [units]="{from: 'hour', to: 'hour'}"></app-countdown-timer>
    <h5>Just minutes</h5>
<pre>
&lt;app-countdown-timer <b>[units]="&#123;from: 'minute', to: 'minute'&#125;"</b> [end]="countdownDate"&gt;&lt;/app-countdown-timer&gt;
</pre>
    <app-countdown-timer [end]="countdownDate" [units]="{from: 'minute', to: 'minute'}"></app-countdown-timer>
    <h5>Just seconds</h5>
<pre>
&lt;app-countdown-timer <b>[units]="&#123;from: 'second', to: 'second'&#125;"</b> [end]="countdownDate"&gt;&lt;/app-countdown-timer&gt;
</pre>
    <app-countdown-timer [end]="countdownDate" [units]="{from: 'second', to: 'second'}"></app-countdown-timer>
  </section>
  <section class="showcase-section">
    <h2>Styling</h2>
    <p>
      You can change different properties to adjust the look and feel of the component.
    </p>
    <h5>Fill</h5>
<pre>
app-countdown-timer &#123;
  --countdown-fill-border: none;
  --countdown-fill-border-radius: 10px;
  --countdown-fill-background: #FFFFFF;
  --countdown-fill-shadow: 0px 0px 5px 0px rgba(0,0,0, 0.2);
&#125;
</pre>
    <app-countdown-timer style="
      background-color: transparent;
      --countdown-padding: 10px;
      --countdown-fill-border: none;
      --countdown-fill-border-radius: 10px;
      --countdown-fill-background: #FFFFFF;
      --countdown-fill-shadow: 0px 0px 5px 0px rgba(0,0,0, 0.2);
    " fill="countdown" [end]="countdownDate" [units]="{from: 'hour', to: 'second'}"></app-countdown-timer>
    <h5>Color</h5>
<pre>
app-countdown-timer &#123;
  --countdown-fill-background: #000000;
  --countdown-value-color: #FFFFFF;
  --countdown-unit-color: #F2F2F2;
&#125;
</pre>
    <app-countdown-timer style="
      --countdown-fill-background: #000000;
      --countdown-padding: 10px;
      --countdown-value-color: #FFFFFF;
      --countdown-unit-color: #F2F2F2;
    " fill="countdown" [end]="countdownDate" [units]="{from: 'hour', to: 'second'}"></app-countdown-timer>
    <h5>Margins & Paddings</h5>
<pre>
app-countdown-timer &#123;
  --countdown-margin: 0px;
  --countdown-padding: 0px;
  --countdown-time-margin: 0px;
  --countdown-time-padding: 0px;
  --countdown-inner-time-margin: 2px;
  --countdown-inner-time-padding: 0px;
&#125;
</pre>
    <h5>Time unit positioning</h5>
    <p>
      Time units to the right (default)
    </p>
<pre>
app-countdown-timer &#123;
  --countdown-time-flex-direction: row-reverse;
&#125;
</pre>
    <app-countdown-timer style="
      --countdown-time-flex-direction: row-reverse;
    " [end]="countdownDate" [units]="{from: 'hour', to: 'hour'}"></app-countdown-timer>
    <p>
      Time units to the left
    </p>
<pre>
app-countdown-timer &#123;
  --countdown-time-flex-direction: row;
&#125;
</pre>
    <app-countdown-timer style="
      --countdown-time-flex-direction: row;
    " [end]="countdownDate" [units]="{from: 'hour', to: 'hour'}"></app-countdown-timer>
    <p>
      Time units above
    </p>
<pre>
app-countdown-timer &#123;
  --countdown-time-flex-direction: column;
&#125;
</pre>
    <app-countdown-timer style="
      --countdown-time-flex-direction: column;
    " [end]="countdownDate" [units]="{from: 'hour', to: 'hour'}"></app-countdown-timer>
    <p>
      Time units below
    </p>
<pre>
app-countdown-timer &#123;
  --countdown-time-flex-direction: column-reverse;
&#125;
</pre>
    <app-countdown-timer style="
      --countdown-time-flex-direction: column-reverse;
    " [end]="countdownDate" [units]="{from: 'hour', to: 'hour'}"></app-countdown-timer>
    <p>
      This component can be configured with different fill modes (by default, no fill will be applied).
    </p>
    <h5>Fill: Entire Countdown</h5>
<pre>
&lt;app-countdown-timer <b>fill="countdown"</b> [end]="countdownDate" [units]="&#123;from: 'hour', to: 'second'&#125;"&gt;&lt;/app-countdown-timer&gt;
</pre>
    <app-countdown-timer style="
    background-color: transparent;
    --countdown-padding: 10px;
    --countdown-fill-border: none;
    --countdown-fill-border-radius: 10px;
    --countdown-fill-background: #FFFFFF;
    --countdown-fill-shadow: 0px 0px 5px 0px rgba(0,0,0, 0.2);
    " fill="countdown" [end]="countdownDate" [units]="{from: 'hour', to: 'second'}"></app-countdown-timer>
    <h5>Fill: Time wrapper</h5>
<pre>
&lt;app-countdown-timer <b>fill="time"</b> [end]="countdownDate" [units]="&#123;from: 'hour', to: 'second'&#125;"&gt;&lt;/app-countdown-timer&gt;
</pre>
    <app-countdown-timer style="
    background-color: transparent;
    --countdown-padding: 10px;
    --countdown-time-margin: 0px 10px;
    --countdown-time-padding: 10px 0px;
    --countdown-fill-border: none;
    --countdown-fill-border-radius: 10px;
    --countdown-fill-background: #FFFFFF;
    --countdown-fill-shadow: 0px 0px 5px 0px rgba(0,0,0, 0.2);
    " fill="time" [end]="countdownDate" [units]="{from: 'hour', to: 'second'}"></app-countdown-timer>
    <h5>Fill: Inner Time Value</h5>
<pre>
&lt;app-countdown-timer <b>fill="inner-time"</b> [end]="countdownDate" [units]="&#123;from: 'hour', to: 'second'&#125;"&gt;&lt;/app-countdown-timer&gt;
</pre>
    <app-countdown-timer style="
    background-color: transparent;
    --countdown-padding: 10px;
    --countdown-inner-time-margin: 5px;
    --countdown-inner-time-padding: 10px;
    --countdown-fill-border: none;
    --countdown-fill-border-radius: 10px;
    --countdown-fill-background: #FFFFFF;
    --countdown-fill-shadow: 0px 0px 5px 0px rgba(0,0,0, 0.2);
    " fill="inner-time" [end]="countdownDate" [units]="{from: 'hour', to: 'second'}"></app-countdown-timer>
  </section>
</ion-content>
